import React, { useState, useEffect } from "react";
// import { forms } from "./Form.js";
import Form from "./Form.js";
import { tokensState } from "./state.js";
import { useRecoilState } from "recoil";

function ShowPortfolio() {
  const [investments, setInvestments] = useState([]);
  const [tokens, setTokens] = useRecoilState(tokensState);
  useEffect(() => {
    fetch("http://localhost:3000/api/crypto", {
      mode: "cors",
    })
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setInvestments(data);
        console.log(data);
      });
  }, [tokens]);

  const deleteBtn = (event) => {
    event.preventDefault();
    let id = event.currentTarget.id;
    fetch(`http://localhost:3000/api/crypto/${id}`, {
      method: "DELETE",
    }).then(() => {
      setInvestments({ id: 0 });
      setTokens({ tokensState });
    });
    console.log(tokens);
  };

  if (investments.id === 0) {
    return null;
  }

  return (
    <>
      <Form />
      <table key="token">
        <thead>
          <tr>
            <th></th>
            <th></th>
            <th></th>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {investments.map((investment) => {
            return (
              // <table key="name">
              // <tbody>
              <tr key={investment.id}>
                <td key="name" value={investment.name}>
                  {investment.name}
                </td>
                <td key="amountInvested" value={investment.amount_invested}>
                  {investment.amount_invested}
                </td>
                <td key="priceAtPurchase" value={investment.price_at_purchase}>
                  {investment.price_at_purchase}
                </td>
                <td key="datePurchased" value={investment.date_purchased}>
                  {investment.date_purchased}
                </td>
                <td>
                  <button
                    id={investment.id}
                    onClick={deleteBtn}
                    className="deleteBtn"
                  >
                    Delete
                  </button>
                </td>
              </tr>
              // </tbody>
              // </table>
            );
          })}
        </tbody>
      </table>
    </>
  );
}

export default ShowPortfolio;
