import React, { useState, useEffect } from "react";
import { forms } from "Form.js";
import Form from "Form.js";

function ShowPortfolio() {
  const [investments, setInvestments] = useState([]);
  const [tokens, setTokens] = useState([]);
  useEffect(() => {
    fetch("http://localhost:3000/api/crypto", {
      mode: "cors",
    })
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setInvestments(data);
        console.log(data);
      });
  }, [tokens, props.forms]);

  const deleteBtn = (event) => {
    event.preventDefault();
    let id = event.currentTarget.id;
    fetch(`http://localhost:3000/api/crypto/${id}`, {
      method: "DELETE",
    }).then(() => {
      setInvestments({ id: 0 });
      setTokens({ id: 0 });
    });
    console.log(tokens);
  };

  if (investments.id === 0) {
    return null;
  }

  return (
    <table key="token">
      <tbody>
        <Form form={forms} />
        {investments.map((investment) => {
          return (
            // <table key="name">
            // <tbody>
            <tr>
              <td key="name">{investment.name}</td>
              <td key="amountInvested">{investment.amount_invested}</td>
              <td key="priceAtPurchase">{investment.price_at_purchase}</td>
              <td key="datePurchased">{investment.date_purchased}</td>
              <button
                id={investment.id}
                onClick={deleteBtn}
                className="deleteBtn"
              >
                X
              </button>
            </tr>
            // </tbody>
            // </table>
          );
        })}
      </tbody>
    </table>
  );
}

export default ShowPortfolio;
