import React, { useEffect, useState, useRecoilState } from "react";
import axios from "axios";
function Form() {
  const [name, setName] = useState("");
  const [tokens, setToken] = useState("");

  const updateInput = (event) => {
    setName(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    axios({
      url: "http://localhost:3000/api/crypto",
      method: "Post",
      data: { name },
    }).then((res) => {
      console.log(res);
    });

    fetch("http://localhost:3000/api/crypto", {
      method: "POST",
      headers: {
        "Content-Type": "application.json",
      },
      body: JSON.stringify({ name }),
    })
      .then((response) => response.json())
      .then((token) => {
        console.log("after post request", token);
        setToken([...tokens, token]);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        <input
          type="text"
          className="crypto"
          onChange={updateInput}
          placeholder="TokenName"
        />
        <input
          type="text"
          className="crypto"
          onChange={updateInput}
          placeholder="USD Invested"
        />
        <input
          type="text"
          className="crypto"
          onChange={updateInput}
          placeholder="Price At Purchase"
        />
        <input
          type="text"
          className="crypto"
          onChange={updateInput}
          placeholder="Token Amount"
        />
        <input
          type="text"
          className="crypto"
          onChange={updateInput}
          placeholder="MM-DD-YYYY"
        />
        <input type="submit" id="submit" />
      </label>
    </form>
  );
}
export default Form;
