import React, { useState, useEffect, Fragment } from "react";
import Form from "./Form.js";
import { tokensState } from "./state.js";
import { useRecoilState } from "recoil";
import ReadOnly from "./ReadOnly.js";
import { Edit } from "./Edit.js";
// import { forms } from "./Form.js";

function ShowPortfolio() {
  const [investments, setInvestments] = useState([]);
  const [tokens, setTokens] = useRecoilState(tokensState);

  const [editInvestmentId, setEditInvestmentId] = useState(null);
  const [editFormData, setEditFormData] = useState({
    name: "",
    amount_invested: "",
    price_at_purchase: "",
    date_purchased: "",
    tokens_owned: "",
  });

  useEffect(() => {
    fetch("http://localhost:3000/api/crypto", {
      mode: "cors",
    })
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setInvestments(data);
        console.log(data);
      });
  }, [tokens, editInvestmentId]);

  const deleteBtn = (event) => {
    event.preventDefault();
    let id = event.currentTarget.id;
    fetch(`http://localhost:3000/api/crypto/${id}`, {
      method: "DELETE",
    }).then(() => {
      setInvestments({ id: 0 });
      setTokens({ tokensState });
    });
    console.log(tokens);
  };

  if (investments.id === 0) {
    return null;
  }

  const handleEditClick = (event, investment) => {
    event.preventDefault();
    setEditInvestmentId(investment.id);
  };

  const handleEditFormChange = (event) => {
    event.preventDefault();
    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;

    const newFormData = { ...editFormData };
    newFormData[fieldName] = fieldValue;

    setEditFormData(newFormData);
  };

  return (
    <>
      <Form className="form" />
      <div className="table">
        <>
          <table key="token">
            <thead>
              <tr>
                <th>Cyptocurrency</th>
                <th>Amount Invested</th>
                <th>Price at Purchase</th>
                <th>Token Amount</th>
                <th>Date of Purchase</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {investments.map((investment) => (
                <Fragment>
                  {editInvestmentId === investment.id ? (
                    <Edit />
                  ) : (
                    <ReadOnly
                      investment={investment}
                      handleEditClick={handleEditClick}
                      deleteBtn={deleteBtn}
                    />
                  )}
                </Fragment>
              ))}
            </tbody>
          </table>
        </>
      </div>
    </>
  );
}

export default ShowPortfolio;

