import React from "react";
// import { tokenState } from "./state.js";
// import { useRecoilState } from "recoil";

export const Edit = ({
  editFormData,
  handleEditFormChange,
  handleCancelClick,
  setEditFormData,
}) => {
  return (
    <tr>
      <td>
        <input
          type="text"
          required="required"
          placeholder="name"
          name="name"
          onChange={(e) => {
            setEditFormData(e.target.value);
          }}
          value={editFormData.name}
        />
      </td>
      <td>
        <input
          type="text"
          required="required"
          placeholder="amount-invested"
          name="amount_invested"
          onChange={(e) => {
            setEditFormData(e.target.value);
          }}
          value={editFormData.amount_invested}
        />
      </td>
      <td>
        <input
          type="text"
          required="required"
          placeholder="price-at-purchase"
          name="price_at_purchase"
          onChange={(e) => {
            setEditFormData(e.target.value);
          }}
          value={editFormData.price_at_purchase}
        />
      </td>
      <td>
        <input
          type="text"
          required="required"
          placeholder="token-amount"
          name="tokens_owned"
          onChange={(e) => {
            setEditFormData(e.target.value);
          }}
          value={editFormData.tokens_owned}
        />
      </td>
      <td>
        <input
          type="text"
          required="required"
          placeholder="date-of-purchase"
          name="date_purchased"
          onChange={(e) => {
            setEditFormData(e.target.value);
          }}
          value={editFormData.date_purchased}
        />
      </td>
      <td className="actions">
        <button type="submit" className="button">
          Save
        </button>
        <button type="button" className="button" onClick={handleCancelClick}>
          Cancel
        </button>
      </td>
    </tr>
  );
};

